import React from "react";

import "./icon-buttons.css";

export const ListButton = ({ isActive, onClick }) => {
  return (
    <div className="icon-btn" onClick={onClick}>
      <svg
        className="list-icon"
        viewBox="0 0 100 100"
        xmlns="http://www.w3.org/2000/svg"
      >
        <line
          className="svg-stroke"
          x1="0"
          y1="10"
          x2="100"
          y2="10"
          stroke={isActive ? "#e74c3c" : "#55efc4"}
          stroke-width="20"
        />
        <line
          className="svg-stroke"
          x1="0"
          y1="50"
          x2="100"
          y2="50"
          stroke={isActive ? "#e74c3c" : "#55efc4"}
          stroke-width="20"
        />
        <line
          className="svg-stroke"
          x1="0"
          y1="90"
          x2="100"
          y2="90"
          stroke={isActive ? "#e74c3c" : "#55efc4"}
          stroke-width="20"
        />
      </svg>
    </div>
  );
};

